import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from './Search.module.css';

const Search = () => {
  const [peliculas, setPeliculas] = useState([]);
  const [totalPaginas, setTotalPaginas] = useState(0);
  const [paginaActual, setPaginaActual] = useState(1);

  useEffect(() => {
    obtenerPeliculas();
  }, [paginaActual]);

  const obtenerPeliculas = async () => {
    try {
      const response = await axios.get('http://localhost:3001/peliculas', {
        params: {
          pagina: paginaActual,
        },
      });
      const { peliculas, totalPaginas } = response.data;
      setPeliculas(peliculas);
      setTotalPaginas(totalPaginas);
    } catch (error) {
      console.error('Error al obtener las películas:', error);
    }
  };

  const handlePaginaAnterior = () => {
    if (paginaActual > 1) {
      setPaginaActual(paginaActual - 1);
    }
  };

  const handlePaginaSiguiente = () => {
    if (paginaActual < totalPaginas) {
      setPaginaActual(paginaActual + 1);
    }
  };

  return (
    <div>
      <ul>
        {peliculas.map((pelicula) => (
          <li key={pelicula.id}>
            <h3>{pelicula.id}</h3>
            <p>{pelicula.description}</p>
            <p>{pelicula.premiere}</p>
          </li>
        ))}
      </ul>
      <div>
        <button onClick={handlePaginaAnterior} disabled={paginaActual === 1}>
          Anterior
        </button>
        <button onClick={handlePaginaSiguiente} disabled={paginaActual === totalPaginas}>
          Siguiente
        </button>
      </div>
      <p>Página {paginaActual} de {totalPaginas}</p>
    </div>
  );
};

export default Search;
