import React, { useState } from 'react';
import axios from 'axios';
import styles from './Form.module.css';

const Form = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [importedData, setImportedData] = useState([]);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = (event) => {
    event.preventDefault(); // Evita el envío predeterminado del formulario

    if (selectedFile) {
      const formData = new FormData();
      formData.append('csvFile', selectedFile);

      axios.post('http://localhost:3001/importarPeliculas', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
    })
        .then(response => {
          // Handle successful upload
          console.log('Imported:', response.data.importadas);
        })
        .catch(error => {
          // Handle upload error
          console.error('Error uploading file:', error);
        });
    }
  };

  return (
    <div className={styles.container}>
      <input className={styles.input} type="file" accept=".csv" onChange={handleFileChange} />
      <button className={styles.button} onClick={handleUpload}>Upload</button>

      {importedData.length > 0 && (
        <div>
          <h2>Datos Importados</h2>
          <ul>
            {importedData.map((fila, index) => (
              <li key={index}>
                <strong>Título: </strong>{fila[0]}<br />
                <strong>Descripción: </strong>{fila[1]}<br />
                <strong>Año: </strong>{fila[2]}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Form;
