import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from './Search.module.css';

const Search = () => {
  const [movies, setMovies] = useState([]);
  const [foundMovie, setFoundMovie] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  useEffect(() => {
    fetchMovies();
  }, [currentPage, searchTerm]);
console.log("-------currentPage-----", currentPage);
  const fetchMovies = async () => {
    try {
      const encodedId = encodeURIComponent(searchTerm); // Codificar el valor del parámetro id
      const url = `http://localhost:3001/search?id=${encodedId}&pagina=${currentPage}`;

     const response = await axios.get(url);
     const response2 = {...response.data, pagina: 1}

    //   const response = await axios.get('http://localhost:3001/search', {
    //     params: {
    //       id: searchTerm ,
    //       pagina: currentPage
    //     }
    //   });
      const { peliculaEncontrada, peliculasPaginadas, paginaActual, totalPaginas } = response2;

      setFoundMovie(peliculaEncontrada);
      setMovies(peliculasPaginadas);
      setCurrentPage(paginaActual);
      setTotalPages(totalPaginas);
    } catch (error) {
      console.error('Error fetching movies:', error);
    }
  };

  const handleSearchChange = (event) => {
    event.preventDefault();
    setSearchTerm(event.target.value);
  };

  const handleSearch = () => {
    setCurrentPage(1);
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };
  console.log("----peliculaEncontrada----",FoundMovie);

  return (
    <div className={styles.container}>
      <h2>Listado de Películas</h2>
      <div className={styles.search}>
        <input
          type="text"
          placeholder="Buscar por título"
          value={searchTerm}
          onChange={handleSearchChange}
        />
        <button onClick={handleSearch}>Buscar</button>
      </div>

      {movies ? (
  <ul className={styles.movieList}>
    {movies.peliculaEncontrada ? (
      <li key={movies.peliculaEncontrada.id} className={styles.movieItem}>
        <span>Título: {movies.peliculaEncontrada.id}</span>
        <span>Descripción: {movies.peliculaEncontrada.description}</span>
        <span>Año: {movies.peliculaEncontrada.premiere}</span>
      </li>
    ) : (
      movies.map((movie) => (
        <li key={movie.id} className={styles.movieItem}>
          <span>Título: {movie.id}</span>
          <span>Descripción: {movie.description}</span>
          <span>Año: {movie.premiere}</span>
        </li>
      ))
    )}
  </ul>
) : (
  <p>No se encontraron películas.</p>
)}








      {totalPages > 1 && (
        <div className={styles.pagination}>
          {Array.from({ length: totalPages }, (_, index) => index + 1).map((page) => (
            <button
              key={page}
              onClick={() => handlePageChange(page)}
              className={currentPage === page ? styles.activePage : ''}
            >
              {page}
            </button>
          ))}
        </div>
      )}
    </div>
  );
};

export default Search;
